import Parser from './lib/Parser';
import Builder from './lib/Builder';

export function parse(xml, options) {
  return (new Parser(options)).parse(xml);
}

export function build(doc, options) {
  return (new Builder(options)).build(doc);
}

import prettify from 'prettify-xml';

const build = Symbol('Builder.build');
const selfClosing = Symbol('Builder.isSelfClosing');

export default class Builder {

  static get build() { return build; }
  static get selfClosing() { return selfClosing; }

  static get defaultIndent() {
    return '  ';
  }

  static get defaultNewline() {
    return '\\n\n';
  }

  constructor(options = {}) {
    this._options = options;

    if (this._options.indent === undefined) {
      this._options.indent = this.constructor.defaultIndent;
    } else if (typeof options.indent === 'number') {
      this._options.indent = ' '.repeat(options.indent);
    }

    if (this._options.newline === undefined) {
      this._options.newline = this.constructor.defaultNewline;
    }
  }

  build(doc) {
    const xml = doc[build](Object.assign({}, this._options, { level: 0 }));

    /* if (this._options) {
      return prettify(xml, this._options);
    } */

    return xml;
  }

}

import { parser as createParser } from 'sax';
import Node from './dom/Node';
import Document from './dom/Document';
import Element from './dom/Element';
import TextNode from './dom/TextNode';
import CDataSection from './dom/CDataSection';

const parsed = originalClass => class extends originalClass {

  constructor(nodeOptions, parser) {
    super(nodeOptions);

    this._position = parser.currentPosition;
  }

  get [Node.info]() {
    return Object.assign(this._position, super[Node.info]);
  }

};

const ParsedElement = parsed(Element);
const ParsedTextNode = parsed(TextNode);
const ParsedCDataSection = parsed(CDataSection);

export default class Parser {

  constructor(options = {}) {
    this._options = Object.assign({}, Parser.defaultOptions, options);
    this._parser = createParser(this._options);

    this._parser.onprocessinginstruction = this.onProcessingInstruction.bind(this);
    this._parser.ontext = this.onText.bind(this);
    this._parser.onopentag = this.onOpenTag.bind(this);
    this._parser.onclosetag = this.onCloseTag.bind(this);
    this._parser.oncdata = this.onCData.bind(this);

    this._processingInstructions = [];
    this._currentElement = null;
    this._rootElement = null;

    // TODO: Remove
    this._indent = 0;
  }

  parse(string) {
    return new Promise((resolve, reject) => {
      this._parser.onerror = err => reject(err);

      this._parser.write(string);
      this._parser.close();

      resolve(new Document({
        root: this._rootElement,
        processingInstructions: this._processingInstructions,
      }));
    });
  }

  get currentPosition() {
    return {
      line: this._parser.line,
      column: this._parser.column,
      position: this._parser.position,
    };
  }

  onProcessingInstruction(pi) {
    this._processingInstructions.push(pi);
    console.log('<?' + pi.name, pi.body, '?>');
  }

  onText(text) {
    if (text.trim().length > 0) {
      if (!this._currentElement) {
        throw new Error('Found text node outside an element');
      }

      this._currentElement.appendChild(new ParsedTextNode({
        parent: this._currentElement,
        text,
      }, this));

      console.log('  '.repeat(this._indent) + '#text');
    }
    // console.log('TEXT', text);
  }

  onOpenTag(tag) {
    const elm = new ParsedElement(Object.assign(tag, {
      parent: this._currentElement,
    }), this);

    if (this._currentElement) {
      this._currentElement.appendChild(elm);
    } else {
      if (this._rootElement) {
        throw new Error('Found second root element');
      }

      this._rootElement = elm;
    }

    this._currentElement = elm;
    console.log('  '.repeat(this._indent++) + '<' + tag.name + '>');
  }

  onCloseTag(tag) {
    this._currentElement = this._currentElement.parentNode;
    console.log('  '.repeat(--this._indent) + '</' + tag + '>');
  }

  onCData(cdata) {
    this._currentElement.appendChild(new ParsedCDataSection({
      parent: this._currentElement,
      text: cdata,
    }, this));
    console.log('  '.repeat(this._indent) + '#cdata', cdata);
  }

}

import TextNode from './TextNode';
import Builder from '../Builder';

export default class CDataSection extends TextNode {

  get tagName() {
    return '#cdata';
  }

  [Builder.build](options) {
    return `<![CDATA[${super[Builder.build](options)}]]>`;
  }

}

import Node from './Node';
import Element from './Element';
import Builder from './../Builder';

export default class Document extends Node {

  constructor(options) {
    super(options);

    this._processingInstructions = options.processingInstructions || [];
    this._documentElement = options.root;
    this._childNodes = [options.root];

    // Add document element getters

    Element[Element.elementGetters].forEach(name => {
      this[name] = Element.prototype[name].bind(this._documentElement);
    });
  }

  // Additional methods

  get documentElement() {
    return this._documentElement;
  }

  createElement(name) {
    return new Element({ name });
  }

  // Abstract Node methods

  get tagName() {
    return '#document';
  }

  get [Node.info]() {
    return this._processingInstructions
      .reduce((result, { name, body }) => Object.assign(result, { [name]: body }), {});
  }

  // Builder

  [Builder.build](options) {
    const pis = this._processingInstructions
      .map(({ name, body }) => `<?${name} ${body}?>`);

    return `${pis}${options.newline}${this._documentElement[Builder.build](options)}`;
  }

}

import Node from './Node';
import TextNode from './TextNode';
import Builder from '../Builder';

const recursiveChildElements = Symbol('Element.recursiveChildElements');
const elementGetters = Symbol('Element.elementGetters');

export class ClassList {

  constructor(element) {
    this._classes = element.className.split(' ')
      .map(n => n.trim())
      .filter(n => n.length !== 0);

    this._element = element;
  }

  item(index) {
    return this._classes[index];
  }

  contains(className) {
    return this._classes.includes(className.trim());
  }

  add(...classNames) {
    classNames.forEach(name => {
      if (!this.contains(name)) {
        this._classes.push(name.trim());
      }
    });

    this._updateClassName();
  }

  _updateClassName() {
    this._element.setAttribute('class', this._classes.join(' '));
  }

}

export default class Element extends Node {

  // Symbols

  static get recursiveChildElements() { return recursiveChildElements; }
  static get elementGetters() { return elementGetters; }

  constructor(options) {
    super(options);

    this._tagName = options.name;
    this._attributes = options.attributes || {};

    // this._childNodes = [];
  }

  // Additional methods

  * [recursiveChildElements]() {
    const c = this.children;

    if (c.length === 0) { return; }

    for (let i = 0; i < c.length; i++) {
      const child = c[i];

      yield child;

      if (child.children.length) {
        yield* child[recursiveChildElements]();
      }
    }
  }


  // - Child elements

  get children() {
    return this._childNodes.filter(n => (n instanceof Element));
  }

  appendChild(elm) {
    if (elm.parentNode) {
      elm.parentNode.removeChild(elm);
    }

    elm._parent = this;
    this._childNodes.push(elm);
  }

  removeChild(elm) {
    if (this._childNodes.includes(elm)) {
      elm._parent = null;
      this._childNodes = this._childNodes.filter(n => n !== elm);
    }

    return elm;
  }

  // - Attributes

  get attributes() {
    return this._attributes;
  }

  getAttribute(name) {
    return this._attributes[name] || null;
  }

  setAttribute(name, value) {
    return (this._attributes[name] = value);
  }

  get className() {
    return this._attributes.class || '';
  }

  set className(className) {
    return (this._attributes.class = className);
  }

  get classList() {
    return new ClassList(this);
  }

  // - Getters

  static get [elementGetters]() {
    return ['getElementById', 'getElementsByTagName', 'getElementsByClassName'];
  }

  getElementById(id) {
    for (const node of this[recursiveChildElements]()) {
      if (node.getAttribute('id') === id) {
        return node;
      }
    }

    return null;
  }

  getElementsByTagName(tagName) {
    const result = [];

    for (const node of this[recursiveChildElements]()) {
      if (node.tagName === tagName) {
        result.push(node);
      }
    }

    return result;
  }

  getElementsByClassName(className) {
    const result = [];
    const name = className.trim();

    for (const node of this[recursiveChildElements]()) {
      if (node.classList.contains(name)) {
        result.push(node);
      }
    }

    return result;
  }

  // Abstract node methods

  get tagName() {
    return this._tagName;
  }

  get [Node.tagInfo]() {
    let result = '';

    if (this._attributes.id) {
      result += `#${this._attributes.id}`;
    }

    if (this._attributes.class) {
      result += `.${this._attributes.class}`;
    }

    return result;
  }

  get [Node.info]() {
    return Object.keys(this._attributes)
      .filter(n => n !== 'class' && n !== 'id')
      .reduce((result, n) => Object.assign(result, { [n]: this._attributes[n] }), {});
  }

  // Builder

  get [Builder.selfClosing]() {
    return this._childNodes.length === 0;
  }

  [Builder.build](options) {
    const tagAttributes = Object.keys(this._attributes)
      .map(key => `${key}="${this._attributes[key]}"`)
      .join(' ');

    const tagContent = `${this.tagName}${tagAttributes.length ? ` ${tagAttributes}` : ''}`;

    const indent = options.indent.repeat(options.level);

    if (this[Builder.selfClosing]) {
      return `${indent}<${tagContent} />`;
    }

    const childOptions = Object.assign({}, options, {
      level: options.level + 1,
    });

    return `${indent}<${tagContent}>${this._childNodes
      .map((node, index, array) => {
        let str = node[Builder.build](childOptions);
        const isTextNode = (node instanceof TextNode);

        if (isTextNode) {
          return str;
        }

        if (index === 0) {
          str = `${options.newline}${str}`;
        }

        if (index > 0 && (array[index - 1] instanceof TextNode)) {
          str = str.replace(/^\s*/, '');
        }

        if (index < (array.length - 1) && (array[index - 1] instanceof TextNode)) {
          return str;
        }

        if (index === array.length - 1) {
          return `${str}${options.newline}${indent}`;
        }

        return `${str}${options.newline}`;
      })
      .join('')
    }</${this.tagName}>`;
  }

}

import { inspect } from 'util';
import Builder from '../Builder';

const tagInfo = Symbol('Node.tagInfo');
const info = Symbol('Node.info');
const recursiveChildNodes = Symbol('Node.recursiveChildNodes');

export default class Node {

  // Symbols

  static get tagInfo() { return tagInfo; }
  static get info() { return info; }
  static get recursiveChildNodes() { return recursiveChildNodes; }

  constructor(options) {
    this._parent = options.parent;
    this._childNodes = [];
  }

  get parentNode() {
    return this._parent;
  }

  * [recursiveChildNodes]() {
    if (!this._childNodes) { return; }

    for (let i = 0; i < this._childNodes.length; i++) {
      const child = this._childNodes[i];
      yield child;

      if (child._childNodes.length) {
        yield* child[recursiveChildNodes]();
      }
    }
  }

  // Abstract

  get tagName() {
    throw new Error('Must be implemented by all subclasses');
  }

  get [tagInfo]() {
    return '';
  }

  get [info]() {
    return {};
  }

  // Builder

  [Builder.build]() {
    throw new Error('Must be implemented by all subclasses');
  }

  // Inspect

  [inspect.custom](depth, options) {
    const start = `${
      options.stylize(this.tagName, 'special')
    }${
      options.stylize(this[tagInfo], 'date')
    } ${inspect(this[info], {
      breakLength: Infinity,
      colors: options.colors,
    })}`;

    if (depth === null || depth >= 0) {
      const newOptions = Object.assign({}, options, {
        depth: options.depth === null ? null : options.depth - 1,
      });

      const c = this._childNodes.map(n => inspect(n, newOptions)).join('\n');

      return `${start}${c ? `\n${c}`.replace(/\n/g, '\n  ') : ''}`;
    }

    return start;
  }

}

import Node from './Node';
import Builder from '../Builder';

export default class TextNode extends Node {

  constructor(options) {
    super(options);

    this._text = options.text;
  }

  // TODO: Remove
  get textContent() {
    return this._text;
  }

  // Abstract node methods

  get tagName() {
    return '#text';
  }

  // Builder

  [Builder.build](options) {
    return this._text.replace(/\r?\n/, options.newline);
  }

}
